<?php
finalclass UserMapper{ private function __construct(){} public static function map(User$user,array$properties){if(array_key_exists('id',$properties)){$user->setId($properties['id']);}if(array_key_exists('nome',$properties)){$user->setNome($properties['nome']);}if(array_key_exists('login',$properties)){$user->setLogin($properties['login']);}if(array_key_exists('senha',$properties)){$user->setSenha($properties['senha']);}if(array_key_exists('email',$properties)){$user->setEmail($properties['email']);}if(array_key_exists('loja',$properties)){$user->setLoja($properties['loja']);}if(array_key_exists('funcao',$properties)){$user->setFuncao($properties['funcao']);}if(array_key_exists('OMIE_APP_KEY',$properties)){$user->setOMIE_APP_KEY($properties['OMIE_APP_KEY']);}if(array_key_exists('OMIE_APP_SECRET',$properties)){$user->setOMIE_APP_SECRET($properties['OMIE_APP_SECRET']);}if(array_key_exists('empresa',$properties)){$user->setempresa($properties['empresa']);}if(array_key_exists('cnpj',$properties)){$user->setcnpj($properties['cnpj']);}}}?>